    private void btncalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcularActionPerformed
        // TODO add your handling code here:
        Resultados ventana= new Resultados();
        double base,altura,radio;
        
       perimetro=0;
        area=0;
        
        if(cbperimetro.isSelected()==true)
        {
            if(rbcuadrado.isSelected()==true)
            {
                base= Double.parseDouble(txtbase.getText());
                perimetro=4*base;
            }
            else
            if(rbrectangulo.isSelected()==true)
            {
                base= Double.parseDouble(txtbase.getText());
        altura= Double.parseDouble(txtaltura.getText());
       
                perimetro=(altura*2)+(base*2);
                
            }
            else
            if(rbtriangulo.isSelected()==true)
            {
                base= Double.parseDouble(txtbase.getText());
        altura= Double.parseDouble(txtaltura.getText());
       
                perimetro=2*altura+base;
            }
                else
                if(rbcirculo.isSelected()==true)
                {
                    
        radio= Double.parseDouble(txtradio.getText());
                perimetro =(3.1416*2*radio);
                }
        }
        if(cbarea.isSelected()==true)
        {
             if(rbcuadrado.isSelected()==true)
             {
                 base= Double.parseDouble(txtbase.getText());
       
               area=base*base;
             }
               else
            if(rbrectangulo.isSelected()==true)
            {
                base= Double.parseDouble(txtbase.getText());
        altura= Double.parseDouble(txtaltura.getText());
      
                area=base*altura;
            }
                else
            if(rbtriangulo.isSelected()==true)
            {
                base= Double.parseDouble(txtbase.getText());
        altura= Double.parseDouble(txtaltura.getText());
       
                area=(base*altura)/2;
            }
                else
                if(rbcirculo.isSelected()==true)
                {
                   
        radio= Double.parseDouble(txtradio.getText());
                area= 3.1416*radio*radio;
                }
        }
        if(rbcuadrado.isSelected()==true)
               ventana.figura="cuadrado";
            else
            if(rbrectangulo.isSelected()==true)
               ventana.figura="rectangulo";
            else
            if(rbtriangulo.isSelected()==true)
               ventana.figura="triangulo";
            else
                if(rbcirculo.isSelected()==true)
                ventana.figura="circulo";
        
        ventana.a=String.valueOf(area);
        ventana.p=String.valueOf(perimetro);
         ventana.setVisible(true);
    //    this.dispose();
        
    }//GEN-LAST:event_btncalcularActionPerformed

